// MODULES //

import generateID from './../generateID';


// GET USER DATA //

/**
* Retrieves user data from localStorage.
*
* @returns {Object} user object
*/
function getUserData() {
	const userString = localStorage.getItem( 'CMU_ISLE_PersistentData' );
	if ( !userString ) {
		const user = {
			id: generateID( 4 )
		};
		localStorage.setItem( 'CMU_ISLE_PersistentData', JSON.stringify( user ) );
		return user;
	}
	return JSON.parse( userString );
} // end FUNCTION getUserData()


// EXPORTS //

export default getUserData;
